import React, { useState, useEffect, useCallback } from 'react';
import { Filter } from 'lucide-react';
import EventCard from './components/EventCard';
import FilterDrawer from './components/FilterDrawer';
import axios from 'axios';
import LoginModal from './components/LoginModal';
import './App.css';

const API_BASE_URL = "http://localhost:8000"; 
// Replace with your actual Azure details
const CLIENT_ID = "YOUR_MICROSOFT_CLIENT_ID"; 
const REDIRECT_URI = window.location.origin; 

function App() {
  const [isFilterOpen, setIsFilterOpen] = useState(false);
  const [selectedBoard, setSelectedBoard] = useState("");
  const [selectedClub, setSelectedClub] = useState("");
  const [isLoginModalOpen, setIsLoginModalOpen] = useState(false);

  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [user, setUser] = useState(null);
  const [events, setEvents] = useState([]);

  // 1. Verify token with backend
  const verifyToken = useCallback(async (token) => {
    try {
      const response = await axios.get(`${API_BASE_URL}/auth/me`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setUser(response.data);
      setIsLoggedIn(true);
    } catch (err) {
      localStorage.removeItem('access_token');
      setIsLoggedIn(false);
    }
  }, []);

  // 2. Exchange Authorization Code for JWT
  const exchangeCodeForToken = useCallback(async (code) => {
    try {
      const response = await axios.post(`${API_BASE_URL}/auth/login/microsoft`, {
        code: code // Matches your LoginRequest schema
      });

      const { access_token, user: userData } = response.data;
      localStorage.setItem('access_token', access_token);
      setUser(userData);
      setIsLoggedIn(true);
      setIsLoginModalOpen(false);

      // Clean URL parameters
      window.history.replaceState({}, document.title, "/");
    } catch (err) {
      console.error("Auth failed:", err.response?.data?.detail || "Unknown error");
      alert("Verification failed. Please use your @iitd.ac.in account.");
    }
  }, []);

  // 3. Lifecycle: Check token or handle OAuth callback
  useEffect(() => {
    const token = localStorage.getItem('access_token');
    const urlParams = new URLSearchParams(window.location.search);
    const code = urlParams.get('code');

    if (code) {
      exchangeCodeForToken(code);
    } else if (token) {
      verifyToken(token);
    }

    // Initial events fetch
    fetchEvents();
  }, [verifyToken, exchangeCodeForToken]);

  const fetchEvents = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/events`); // Matches documentation
      setEvents(response.data);
    } catch (err) {
      console.error("Failed to fetch events");
    }
  };

  const handleLogin = () => {
    const SCOPE = encodeURIComponent("openid profile email");
    const encodedRedirect = encodeURIComponent(REDIRECT_URI);

    // Redirecting to Microsoft Authorization Endpoint
    window.location.href = `https://login.microsoftonline.com/common/oauth2/v2.0/authorize?client_id=${CLIENT_ID}&response_type=code&redirect_uri=${encodedRedirect}&response_mode=query&scope=${SCOPE}`; 
  };

  const filteredEvents = events.filter(event => {
    const boardMatch = selectedBoard ? event.board === selectedBoard : true;
    const clubMatch = selectedClub ? event.club === selectedClub : true;
    return boardMatch && clubMatch;
  });

  return (
    <div className="container-fluid">
      <div className="row">
        <nav className="col-md-2 d-none d-md-block sidebar">
          <h4 className="p-3 fw-bold text-purple">SYNAPSE</h4>
          <ul className="nav flex-column mt-4">
            <li className="nav-item mb-2"><a href="#" className="nav-link text-white active">Events</a></li>
          </ul>
        </nav>

        <main className="col-md-10 ms-sm-auto px-md-4 py-4">
          <div className="d-flex justify-content-between align-items-center mb-5">
            <div>
              <h1 className="h2 fw-bold text-white mb-0">Upcoming Events</h1>
              <p className="text-secondary mb-0">Find and register for club activities</p>
            </div>

            <div className="d-flex gap-3 align-items-center position-relative">
              <button 
                className={`btn d-flex align-items-center gap-2 ${selectedBoard || selectedClub ? 'btn-purple' : 'btn-outline-secondary text-white'}`}
                onClick={() => setIsFilterOpen(true)}
              >
                <Filter size={18} />
                Filter {filteredEvents.length < events.length && `(${filteredEvents.length})`}
              </button>

              {!isLoggedIn ? (
                <button 
                  className="btn btn-purple px-4 fw-bold shadow-sm"
                  onClick={() => setIsLoginModalOpen(true)}
                >
                  Login
                </button>
              ) : (
                <div className="user-profile-circle shadow-sm" title={user?.name}>
                  {user?.name?.substring(0, 2).toUpperCase() || '??'}
                </div>
              )}

              <LoginModal 
                isOpen={isLoginModalOpen} 
                onClose={() => setIsLoginModalOpen(false)} 
                onLogin={handleLogin}
              />
            </div>
          </div>

          <div className="row">
            {filteredEvents.length > 0 ? (
              filteredEvents.map(event => (
                <div key={event.id} className="col-md-4 mb-4">
                  <EventCard 
                    event={event} 
                    isLoggedIn={isLoggedIn} 
                    onLoginRequired={() => setIsLoginModalOpen(true)} 
                  />
                </div>
              ))
            ) : (
              <div className="col-12 text-center py-5 glass-card rounded-4">
                <h4 className="text-secondary">No events found for this selection</h4>
                <button className="btn btn-link text-purple" onClick={() => {setSelectedBoard(""); setSelectedClub("");}}>
                  Clear all filters
                </button>
              </div>
            )}
          </div>
        </main>
      </div>

      <FilterDrawer 
        isOpen={isFilterOpen}
        onClose={() => setIsFilterOpen(false)}
        selectedBoard={selectedBoard}
        setSelectedBoard={setSelectedBoard}
        selectedClub={selectedClub}
        setSelectedClub={setSelectedClub}
      />
    </div>
  );
}

export default App;
